const Log = require('./src/log');

module.exports = (app = {}, config = {}) => {
  if (app.config.cluster.cpus > 1) {
    Log.configure(config['cluster']);
  } else {
    Log.configure(config['single']);
  }
  app.Log = Log.logger();
  if (app.addAppProp) app.addAppProp('Log', app.Log);
  return app;
}
